import React from 'react';
import { rootSaga } from './src/common/sagas/RootSaga';
import reducer from './src/common/reducers/reducer';
import { createStore, applyMiddleware } from 'redux';
import createSagaMiddleware from 'redux-saga';
import {composeWithDevTools} from 'redux-devtools-extension';
import {setAxiosInterceptor} from './src/common/helpers/axiosHelper';
import {handleSagaError} from "./src/errorHandling/SagaErrorHandler";
import {initExceptionHandler} from "./src/errorHandling/GlobalExceptionHandler";


const sagas = createSagaMiddleware({onError: handleSagaError});
const store = createStore(reducer, composeWithDevTools(applyMiddleware(sagas)));
setAxiosInterceptor(store);
initExceptionHandler();

sagas.run(rootSaga);


export default store;
