import React from 'react';
import axios from "axios";
import {Table} from "./Table";
import {compoundSortComparator, secondSortComparator} from "./Utilities";

const availabilitySort = (a, b) => {
    if (a.status === 'ACTIVE' && b.status !== 'ACTIVE') {
        return -1;
    }

    if (a.status !== 'ACTIVE' && b.status === 'ACTIVE') {
        return 1;
    }

    return 0;
};

export class MyCountryList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            countries: []
        }
    }

    async componentDidMount() {
        const response = await axios.get("https://restcountries.eu/rest/v2/all");
        console.log(response.data);
        this.setState({countries: response.data});
    }

    render() {
        let countries = this.state.countries;

        return (
           <div>

               <Table config={{
                   data: [...countries].sort(availabilitySort),
                   highlights: [
                       {field:"status", condition:"INACTIVE", className:"inactive"},
                   ],
                   fields: [
                       {name: 'name', path: 'name', sortable: true},
                       {name: 'capital', path: 'capital', sortable: true, comparator: compoundSortComparator(['capital','name'])},
                       {name: 'alpha2Code', path: 'alpha2Code', sortable: true, comparator: compoundSortComparator(['alpha2Code','name'])},
                       {name: 'population', path: 'population', sortable: true, comparator: compoundSortComparator(['population','name'])},
                   ]
               }}/>

           </div>
        );
    }
}
